import { Component, Vue, Prop, Provide } from "vue-property-decorator";
import * as firebase from "firebase";

@Component({ name: "Login" })
export default class Login extends Vue {
  form = {
    username: "",
    email: "",
    password: ""
  };
  error = null;
  async signInUser() {
    try {
      console.log("[signInUser] signing in user: ", this.form);
      // create the new user in firebase
      const authData = await firebase
        .auth()
        .signInWithEmailAndPassword(this.form.email, this.form.password);
      // set user in the state
      const userData = {
        displayName: authData.user.displayName,
        email: authData.user.email,
        emailVerified: authData.user.emailVerified,
      }
      this.$store.commit('SET_USER', userData);
      // go to the home page
      this.$router.replace({ name: "UserDashboard", params: { username: authData.user.displayName }});
      console.log("[signInUser] login complete: ", authData);
    } catch (error) {
      this.error = error;
      console.error(error);
      throw error;
    }
  }
}
